import React, { useState } from "react";
import { Slider } from "@/components/ui/slider";
import { Input } from "@/components/ui/input";

export default function PrizeStakingCalculator() {
  // State hooks
  const [totalStaked, setTotalStaked]         = useState(700_000_000);
  const [userStake, setUserStake]             = useState(10000);
  const [industryPercent, setIndustryPercent] = useState(10);
  const [tokenPrice, setTokenPrice]           = useState(0.01);

  // Constants
  const INDUSTRY_TOTAL     = 400_000_000_000;
  const BUSINESS_CUT       = 0.03;
  const MAX_REVENUE        = INDUSTRY_TOTAL * BUSINESS_CUT;
  const COMPOUNDS_PER_YEAR = 365;

  // Core calculations
  const revenueCaptured      = (industryPercent / 100) * MAX_REVENUE;
  const userShare            = userStake / totalStaked;
  const userRevenue          = userShare * revenueCaptured;          // simple APR revenue
  const initialInvestmentUSD = userStake * tokenPrice;               // USD invested

  // Nominal APR (decimal)
  const aprDecimal = userRevenue / initialInvestmentUSD;             // e.g. 171.4286

  // Daily-compounded APY calculations:
  const apyDecimal   = Math.pow(1 + aprDecimal / COMPOUNDS_PER_YEAR, COMPOUNDS_PER_YEAR) - 1;
  const apyRevenue   = initialInvestmentUSD * apyDecimal;           // USD yield with compounding
  const apyPercent   = apyDecimal * 100;                            // APY in %

  // Price for 20% simple APR (unchanged)
  const tokenPriceFor20APR = (revenueCaptured / totalStaked) / 0.20;

  // Helpers
  const formatNumber     = (num) => num.toLocaleString(undefined, { maximumFractionDigits: 2 });
  const formatInputValue = (num) => num.toLocaleString();
  const parseInputValue  = (val) => Number(val.replace(/,/g, ""));

  return (
    <div
      className="calculator-wrapper"
      style={{
        margin: '0 auto',
        padding: '1.5rem',
        maxWidth: '1200px',
        fontFamily: 'Chelsea Market',
        color: 'black',
        boxSizing: 'border-box',
      }}
    >
      <div className="yellow-box">
        <div className="heading-box">
          <h1>$PRIZE Staking Calculator</h1>
        </div>

        <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
          {/* Input Panel */}
          <div className="green-box" style={{ backgroundColor:'#FFF031', border:'none', boxShadow:'none' }}>
            <div style={{ color:'#1427B8', fontSize:'1.5rem' }}>
              <div className="space-y-8 pt-2">

                {/* Total Staked */}
                <div style={{ display:'flex', flexDirection:'column', alignItems:'flex-start' }}>
                  <label className="font-semibold text-blue-600 mb-1">
                    Total $PRIZE Tokens Currently Staked
                  </label>
                  <div style={{ width:'100%', maxWidth:'500px' }}>
                    <Input
                      type="text"
                      value={formatInputValue(totalStaked)}
                      onChange={e => setTotalStaked(parseInputValue(e.target.value))}
                      className="text-black"
                      style={{ fontSize:'1.3rem', padding:'10px', borderRadius:'8px', width:'100%' }}
                    />
                  </div>
                  <p className="text-sm mt-2" style={{ fontSize:'0.9rem' }}>
                    Enter the total number of $PRIZE tokens currently staked on the platform.
                  </p>
                </div>
                <hr className="border-white" />

                {/* Your Stake */}
                <div style={{ display:'flex', flexDirection:'column', alignItems:'flex-start' }}>
                  <label className="font-semibold text-blue-600 mb-1">
                    Your $PRIZE Stake
                  </label>
                  <div style={{ width:'100%', maxWidth:'500px' }}>
                    <Input
                      type="text"
                      value={formatInputValue(userStake)}
                      onChange={e => setUserStake(parseInputValue(e.target.value))}
                      className="text-black"
                      style={{ fontSize:'1.3rem', padding:'10px', borderRadius:'8px', width:'100%' }}
                    />
                  </div>
                  <p className="text-sm mt-2" style={{ fontSize:'0.9rem' }}>
                    Enter how many $PRIZE tokens you are considering to stake.
                  </p>
                </div>
                <hr className="border-white" />

                {/* Token Price */}
                <div style={{ display:'flex', flexDirection:'column', alignItems:'flex-start' }}>
                  <label className="font-semibold text-blue-600 mb-1">
                    $PRIZE Token Price (USD)
                  </label>
                  <div style={{ width:'100%', maxWidth:'500px' }}>
                    <Input
                      type="number"
                      value={tokenPrice}
                      onChange={e => setTokenPrice(parseFloat(e.target.value) || 0)}
                      className="text-black"
                      style={{ fontSize:'1.3rem', padding:'10px', borderRadius:'8px', width:'100%' }}
                    />
                  </div>
                  <p className="text-sm mt-2" style={{ fontSize:'0.9rem' }}>
                    Enter the current market price of one $PRIZE token in USD.
                  </p>
                </div>
                <hr className="border-white" />

                {/* Industry Revenue Capture */}
                <div style={{ display:'flex', flexDirection:'column', alignItems:'flex-start' }}>
                  <label className="font-semibold text-blue-600 mb-2">
                    Industry Revenue Capture (% of $400B)
                  </label>
                  <div style={{ width:'100%', maxWidth:'500px' }}>
                    <Slider
                      min={2} max={100} step={1}
                      value={[industryPercent]}
                      onValueChange={val => setIndustryPercent(val[0])}
                      className="slider-field"
                    />
                  </div>
                  <div className="mt-2">{industryPercent}%</div>
                  <p className="text-sm mt-1" style={{ fontSize:'0.9rem' }}>
                    Adjust to estimate what portion of the $400B annual revenue the industry captures.
                  </p>
                </div>

              </div>
            </div>
          </div>

          {/* Results Panel */}
          <div className="green-box" style={{ width:'100%' }}>
            <div className="text-2xl font-bold text-center text-white" style={{ fontSize:'2.5rem' }}>
              RESULTS
            </div>
            <hr className="border-white" />

            {/* Investment USD */}
            <div>
              <strong style={{ fontSize:'1.5rem' }}>Your Investment (USD):</strong>
              <div style={{ color:'#1427B8', fontSize:'1.5rem', fontWeight:'bold' }}>
                ${formatNumber(initialInvestmentUSD)}
              </div>
              <p className="text-sm text-white" style={{ fontSize:'0.9rem' }}>
                This is the USD value of the $PRIZE tokens you have staked.
              </p>
            </div>
            <hr className="border-white" />

            {/* Daily-Compounded Yield (APY Revenue) */}
            <div>
              <strong style={{ fontSize:'1.5rem' }}>Staking Revenue (USD, APY):</strong>
              <div style={{ color:'#1427B8', fontSize:'1.5rem', fontWeight:'bold' }}>
                ${formatNumber(apyRevenue)}
              </div>
              <p className="text-sm text-white" style={{ fontSize:'0.9rem' }}>
                This is your annual staking yield in USD, compounded daily.
              </p>
            </div>
            <hr className="border-white" />

            {/* APY Percent */}
            <div>
              <strong style={{ fontSize:'1.5rem' }}>APY (%):</strong>
              <div style={{ color:'#1427B8', fontSize:'1.5rem', fontWeight:'bold' }}>
                {formatNumber(apyPercent)}%
              </div>
              <p className="text-sm text-white" style={{ fontSize:'0.9rem' }}>
                This is your annual percentage yield, compounded daily.
              </p>
            </div>
            <hr className="border-white" />

            {/* Price for 20% APR */}
            <div>
              <strong style={{ fontSize:'1.5rem' }}>Price for 20% APR (USD):</strong>
              <div style={{ color:'#1427B8', fontSize:'1.5rem', fontWeight:'bold' }}>
                ${formatNumber(tokenPriceFor20APR)}
              </div>
              <p className="text-sm text-white" style={{ fontSize:'0.9rem' }}>
                This is the required token price to achieve a 20% simple APR across all stakers.
              </p>
            </div>

            <button
              onClick={() => window.open('https://www.prizetech.xyz/buy-prize-ico','_blank')}
              className="button"
              style={{ padding:'12px 48px', width:'calc(28% * 3)', marginTop:'auto', marginBottom:'20px' }}
            >
              BUY $PRIZE
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}
