import React, { useState } from "react";
import { Slider } from "@/components/ui/slider";
import { Input } from "@/components/ui/input";

export default function PrizeStakingCalculator() {
  // ─── User Inputs ────────────────────────────────────────────────
  const [totalStaked, setTotalStaked]         = useState(700_000_000);
  const [userStake, setUserStake]             = useState(10_000);
  const [industryCapture, setIndustryCapture] = useState(10);
  const [tokenPrice, setTokenPrice]           = useState(0.01);

  // ─── Constants ─────────────────────────────────────────────────
  const INDUSTRY_TOTAL  = 400_000_000_000;   // $400 B
  const BUSINESS_CUT    = 0.03;              // 3¢ per $1
  const MAX_POOL_USD    = INDUSTRY_TOTAL * BUSINESS_CUT; // $12 B
  const COMPOUNDS_PER_YEAR = 365;            // daily compounding

  // ─── 1) Apply slider % to pool ─────────────────────────────────
  // e.g. 10% → $1.2 B; 100% → $12 B
  const yieldPoolUSD     = MAX_POOL_USD * (industryCapture / 100);

  // ─── 2) Per-token & user annual USD yield ──────────────────────
  const perTokenYieldUSD = yieldPoolUSD / totalStaked;
  const annualRevenueUSD = perTokenYieldUSD * userStake;       // e.g. $17 142.86

  // ─── 3) Your USD cost basis ────────────────────────────────────
  const initialValueUSD  = userStake * tokenPrice;            // e.g. $100

  // ─── 4) Raw APR fraction (ratio of return) ─────────────────────
  //    e.g. $17 142.86 ÷ $100 = 171.4286
  const aprFraction      = annualRevenueUSD / initialValueUSD;

  // ─── 5) Derive daily rate & true APY ───────────────────────────
  const dailyRate        = aprFraction / COMPOUNDS_PER_YEAR;  
  const apyFraction      = Math.pow(1 + dailyRate, COMPOUNDS_PER_YEAR) - 1;

  // ─── 6) APY outputs ────────────────────────────────────────────
  const apyPercent       = apyFraction * 100;                 // e.g. 453.05%
  const apyRevenueUSD    = initialValueUSD * apyFraction;     // e.g. $453.05

  // ─── 7) Price for 20% APR (unchanged) ─────────────────────────
  // Solve: 0.20 × (totalStaked × x) = yieldPoolUSD
  // ⇒ x = yieldPoolUSD / (0.20 × totalStaked)
  const priceFor20APR    = yieldPoolUSD / (0.20 * totalStaked); // e.g. $8.57

  // ─── Number formatter ──────────────────────────────────────────
  const fmt = n => n.toLocaleString(undefined, { maximumFractionDigits: 2 });

  return (
    <div
      className="mx-auto p-6"
      style={{
        maxWidth: "1200px",
        fontFamily: "Chelsea Market",
        color: "black",
        boxSizing: "border-box"
      }}
    >
      <div className="yellow-box">
        <div className="heading-box">
          <h1>$PRIZE Staking Calculator</h1>
        </div>

        <div className="grid grid-cols-1 md:grid-cols-2 gap-8">

          {/* INPUT PANEL */}
          <div className="green-box" style={{ backgroundColor: "#FFF031", border: "none", boxShadow: "none" }}>
            <div style={{ color: "#1427B8", fontSize: "1.5rem" }} className="space-y-8 pt-2">

              {/* Total Staked */}
              <div style={{ display: "flex", flexDirection: "column", alignItems: "flex-start" }}>
                <label className="font-semibold text-blue-600 mb-1">
                  Total $PRIZE Tokens Currently Staked
                </label>
                <div style={{ width: "100%", maxWidth: "500px" }}>
                  <Input
                    type="text"
                    value={fmt(totalStaked)}
                    onChange={e => setTotalStaked(Number(e.target.value.replace(/,/g, "")))}
                    className="text-black"
                    style={{ fontSize: "1.3rem", padding: "10px", borderRadius: "8px", width: "100%" }}
                  />
                </div>
                <p className="text-sm mt-2" style={{ fontSize: "0.9rem" }}>
                  Enter the total number of $PRIZE tokens currently staked on the platform.
                </p>
              </div>
              <hr className="border-white" />

              {/* Your Stake */}
              <div style={{ display: "flex", flexDirection: "column", alignItems: "flex-start" }}>
                <label className="font-semibold text-blue-600 mb-1">Your $PRIZE Stake</label>
                <div style={{ width: "100%", maxWidth: "500px" }}>
                  <Input
                    type="text"
                    value={fmt(userStake)}
                    onChange={e => setUserStake(Number(e.target.value.replace(/,/g, "")))}
                    className="text-black"
                    style={{ fontSize: "1.3rem", padding: "10px", borderRadius: "8px", width: "100%" }}
                  />
                </div>
                <p className="text-sm mt-2" style={{ fontSize: "0.9rem" }}>
                  Enter how many $PRIZE tokens you are considering to stake.
                </p>
              </div>
              <hr className="border-white" />

              {/* Token Price */}
              <div style={{ display: "flex", flexDirection: "column", alignItems: "flex-start" }}>
                <label className="font-semibold text-blue-600 mb-1">$PRIZE Token Price (USD)</label>
                <div style={{ width: "100%", maxWidth: "500px" }}>
                  <Input
                    type="number"
                    value={tokenPrice}
                    onChange={e => setTokenPrice(parseFloat(e.target.value) || 0)}
                    className="text-black"
                    style={{ fontSize: "1.3rem", padding: "10px", borderRadius: "8px", width: "100%" }}
                  />
                </div>
                <p className="text-sm mt-2" style={{ fontSize: "0.9rem" }}>
                  Enter the current market price of one $PRIZE token in USD.
                </p>
              </div>
              <hr className="border-white" />

              {/* Industry Revenue Slider */}
              <div style={{ display: "flex", flexDirection: "column", alignItems: "flex-start" }}>
                <label className="font-semibold text-blue-600 mb-2">
                  Industry Revenue Capture (% of $400 B)
                </label>
                <div style={{ width: "100%", maxWidth: "500px" }}>
                  <Slider
                    min={2}
                    max={100}
                    step={1}
                    value={[industryCapture]}
                    onValueChange={val => setIndustryCapture(val[0])}
                    className="slider-field"
                  />
                </div>
                <div className="mt-2">{industryCapture}%</div>
                <p className="text-sm mt-1" style={{ fontSize: "0.9rem" }}>
                  Adjust to estimate what portion of the $400 B annual industry revenue the platform captures.
                </p>
              </div>
            </div>
          </div>

          {/* RESULTS PANEL */}
          <div className="green-box" style={{ width: "100%" }}>
            <div className="text-2xl font-bold text-center text-white" style={{ fontSize: "2.5rem" }}>
              RESULTS
            </div>
            <hr className="border-white" />

            {/* Staking Revenue (USD, APY) */}
            <div>
              <strong style={{ fontSize: "1.5rem" }}>Staking Revenue (USD, APY):</strong>
              <div style={{ color: "#1427B8", fontSize: "1.5rem", fontWeight: "bold" }}>
                ${fmt(apyRevenueUSD)}
              </div>
              <p className="text-sm text-white" style={{ fontSize: "0.9rem" }}>
                Your annual staking yield in USD, compounded daily.
              </p>
            </div>
            <hr className="border-white" />

            {/* APY (%) */}
            <div>
              <strong style={{ fontSize: "1.5rem" }}>APY (%):</strong>
              <div style={{ color: "#1427B8", fontSize: "1.5rem", fontWeight: "bold" }}>
                {fmt(apyPercent)}%
              </div>
              <p className="text-sm text-white" style={{ fontSize: "0.9rem" }}>
                Annual percentage yield, compounded daily.
              </p>
            </div>
            <hr className="border-white" />

            {/* Price for 20% APR */}
            <div>
              <strong style={{ fontSize: "1.5rem" }}>Price for 20% APR (USD):</strong>
              <div style={{ color: "#1427B8", fontSize: "1.5rem", fontWeight: "bold" }}>
                ${fmt(priceFor20APR)}
              </div>
              <p className="text-sm text-white" style={{ fontSize: "0.9rem" }}>
                This required token price to achieve a 20% APR across all stakers.
              </p>
            </div>

            <button
              onClick={() => window.open("https://www.prizetech.xyz/buy-prize-ico", "_blank")}
              className="button"
              style={{ padding: "12px 48px", width: "calc(28% * 3)", marginTop: "auto", marginBottom: "20px" }}
            >
              BUY $PRIZE
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}
