import React, { useState } from "react";
import { Slider } from "@/components/ui/slider";
import { Input } from "@/components/ui/input";

export default function PrizeStakingCalculator() {
  // State (defaults unchanged)
  const [totalStaked, setTotalStaked]         = useState(700_000_000);
  const [userStake, setUserStake]             = useState(10000);
  const [industryPercent, setIndustryPercent] = useState(10);
  const [tokenPrice, setTokenPrice]           = useState(0.01);

  // Constants
  const INDUSTRY_TOTAL     = 400_000_000_000;              // $400 B industry
  const BUSINESS_CUT       = 0.03;                         // $0.03 revenue per $1 of industry
  const MAX_REVENUE        = INDUSTRY_TOTAL * BUSINESS_CUT;// $12 B total pool
  const COMPOUNDS_PER_YEAR = 365;                          // daily compounding

  // 1) Compute annual flat revenue (simple base)
  const annualRevenueUSD     =
    (industryPercent / 100) *
    MAX_REVENUE *
    (userStake / totalStaked);
  const initialInvestmentUSD = userStake * tokenPrice;

  // 2) Derive daily rate from that annual revenue
  const dailyRate = (annualRevenueUSD / initialInvestmentUSD) / COMPOUNDS_PER_YEAR;

  // 3) Compound it daily for a true APY
  const apyFraction = Math.pow(1 + dailyRate, COMPOUNDS_PER_YEAR) - 1;
  const apyPercent  = apyFraction * 100;                  // e.g. ~400 %–500 % range
  const apyRevenueUSD = initialInvestmentUSD * apyFraction;// USD earned in a year under APY

  // 4) Price required for 20 % simple-interest return (unchanged)
  const tokenPriceFor20APR =
    ((industryPercent / 100) * MAX_REVENUE / totalStaked) /
    0.20;

  // Formatter
  const fmt = (n) => n.toLocaleString(undefined, { maximumFractionDigits: 2 });

  return (
    <div
      className="calculator-wrapper"
      style={{
        margin: "0 auto",
        padding: "1.5rem",
        maxWidth: "1200px",
        fontFamily: "Chelsea Market",
        color: "black",
        boxSizing: "border-box",
      }}
    >
      <div className="yellow-box">
        <div className="heading-box">
          <h1>$PRIZE Staking Calculator</h1>
        </div>

        <div className="grid grid-cols-1 md:grid-cols-2 gap-8">

          {/* INPUT PANEL */}
          <div
            className="green-box"
            style={{ backgroundColor: "#FFF031", border: "none", boxShadow: "none" }}
          >
            <div style={{ color: "#1427B8", fontSize: "1.5rem" }} className="space-y-8 pt-2">
              {/* Total Staked */}
              <div style={{ display: "flex", flexDirection: "column", alignItems: "flex-start" }}>
                <label className="font-semibold text-blue-600 mb-1">
                  Total $PRIZE Tokens Currently Staked
                </label>
                <div style={{ width: "100%", maxWidth: "500px" }}>
                  <Input
                    type="text"
                    value={fmt(totalStaked)}
                    onChange={(e) =>
                      setTotalStaked(Number(e.target.value.replace(/,/g, "")))
                    }
                    className="text-black"
                    style={{ fontSize: "1.3rem", padding: "10px", borderRadius: "8px", width: "100%" }}
                  />
                </div>
                <p className="text-sm mt-2" style={{ fontSize: "0.9rem" }}>
                  Enter the total number of $PRIZE tokens currently staked on the platform.
                </p>
              </div>
              <hr className="border-white" />

              {/* Your Stake */}
              <div style={{ display: "flex", flexDirection: "column", alignItems: "flex-start" }}>
                <label className="font-semibold text-blue-600 mb-1">
                  Your $PRIZE Stake
                </label>
                <div style={{ width: "100%", maxWidth: "500px" }}>
                  <Input
                    type="text"
                    value={fmt(userStake)}
                    onChange={(e) =>
                      setUserStake(Number(e.target.value.replace(/,/g, "")))
                    }
                    className="text-black"
                    style={{ fontSize: "1.3rem", padding: "10px", borderRadius: "8px", width: "100%" }}
                  />
                </div>
                <p className="text-sm mt-2" style={{ fontSize: "0.9rem" }}>
                  Enter how many $PRIZE tokens you are considering to stake.
                </p>
              </div>
              <hr className="border-white" />

              {/* Token Price */}
              <div style={{ display: "flex", flexDirection: "column", alignItems: "flex-start" }}>
                <label className="font-semibold text-blue-600 mb-1">
                  $PRIZE Token Price (USD)
                </label>
                <div style={{ width: "100%", maxWidth: "500px" }}>
                  <Input
                    type="number"
                    value={tokenPrice}
                    onChange={(e) =>
                      setTokenPrice(parseFloat(e.target.value) || 0)
                    }
                    className="text-black"
                    style={{ fontSize: "1.3rem", padding: "10px", borderRadius: "8px", width: "100%" }}
                  />
                </div>
                <p className="text-sm mt-2" style={{ fontSize: "0.9rem" }}>
                  Enter the current market price of one $PRIZE token in USD.
                </p>
              </div>
              <hr className="border-white" />

              {/* Industry Revenue Capture */}
              <div style={{ display: "flex", flexDirection: "column", alignItems: "flex-start" }}>
                <label className="font-semibold text-blue-600 mb-2">
                  Industry Revenue Capture (% of $400B)
                </label>
                <div style={{ width: "100%", maxWidth: "500px" }}>
                  <Slider
                    min={2}
                    max={100}
                    step={1}
                    value={[industryPercent]}
                    onValueChange={(val) => setIndustryPercent(val[0])}
                    className="slider-field"
                  />
                </div>
                <div className="mt-2">{industryPercent}%</div>
                <p className="text-sm mt-1" style={{ fontSize: "0.9rem" }}>
                  Adjust to estimate what portion of the $400 B annual revenue the platform captures.
                </p>
              </div>
            </div>
          </div>

          {/* RESULTS PANEL */}
          <div className="green-box" style={{ width: "100%" }}>
            <div className="text-2xl font-bold text-center text-white" style={{ fontSize: "2.5rem" }}>
              RESULTS
            </div>
            <hr className="border-white" />

            {/* APY Revenue */}
            <div>
              <strong style={{ fontSize: "1.5rem" }}>Staking Revenue (USD, APY):</strong>
              <div style={{ color: "#1427B8", fontSize: "1.5rem", fontWeight: "bold" }}>
                ${fmt(apyRevenueUSD)}
              </div>
              <p className="text-sm text-white" style={{ fontSize: "0.9rem" }}>
                Your annual staking yield in USD, compounded daily.
              </p>
            </div>
            <hr className="border-white" />

            {/* APY % */}
            <div>
              <strong style={{ fontSize: "1.5rem" }}>APY (%):</strong>
              <div style={{ color: "#1427B8", fontSize: "1.5rem", fontWeight: "bold" }}>
                {fmt(apyPercent)}%
              </div>
              <p className="text-sm text-white" style={{ fontSize: "0.9rem" }}>
                Annual percentage yield, compounded daily.
              </p>
            </div>
            <hr className="border-white" />

            {/* Price for 20% Return */}
            <div>
              <strong style={{ fontSize: "1.5rem" }}>Price for 20% Return (USD):</strong>
              <div style={{ color: "#1427B8", fontSize: "1.5rem", fontWeight: "bold" }}>
                ${fmt(tokenPriceFor20APR)}
              </div>
              <p className="text-sm text-white" style={{ fontSize: "0.9rem" }}>Required token price for a 20% simple-interest annual return.</p>
            </div>

            <button
              onClick={() => window.open("https://www.prizetech.xyz/buy-prize-ico", "_blank")}
              className="button"
              style={{ padding: "12px 48px", width: "calc(28% * 3)", marginTop: "auto", marginBottom: "20px" }}
            >
              BUY $PRIZE
            </button>
          </div>

        </div>
      </div>
    </div>
  );
}
