import React, { useState } from "react";
import { Slider } from "@/components/ui/slider";
import { Input } from "@/components/ui/input";

export default function PrizeStakingCalculator() {
  // — Inputs (defaults) —
  const [totalStaked, setTotalStaked]         = useState(700_000_000);
  const [userStake, setUserStake]             = useState(10000);
  const [industryPercent, setIndustryPercent] = useState(10);
  const [tokenPrice, setTokenPrice]           = useState(0.01);

  // — Constants —
  const INDUSTRY_TOTAL     = 400_000_000_000;              // $400 B industry
  const BUSINESS_CUT       = 0.03;                         // 3¢ per $1
  const MAX_POOL_USD       = INDUSTRY_TOTAL * BUSINESS_CUT;// $12 B total pool
  const COMPOUNDS_PER_YEAR = 365;                          // daily compounding

  // 1) Slider‐adjusted yield pool
  const yieldPoolUSD     = MAX_POOL_USD * (industryPercent / 100); // e.g. $1.2 B
  const perTokenYieldUSD = yieldPoolUSD / totalStaked;            // ≃ $1.7142857
  const annualRevenueUSD = perTokenYieldUSD * userStake;          // ≃ $17 142.86

  // 2) Your USD investment
  const initialValueUSD = userStake * tokenPrice;                // ≃ $100

  // 3) CORRECT APR decimal (raw ratio, not %÷100)
  const aprDecimal = annualRevenueUSD / initialValueUSD;         // ≃ 171.4286

  // 4) True daily‐compounded APY
  const dailyRate   = aprDecimal / COMPOUNDS_PER_YEAR;           // ≃ 0.469856 % per day (as decimal)
  const apyFraction = Math.pow(1 + dailyRate, COMPOUNDS_PER_YEAR) - 1;
  const apyPercent  = apyFraction * 100;                         // ≃ 453.05 %

  // 5) USD yield at APY
  const apyRevenueUSD = initialValueUSD * apyFraction;           // ≃ $453.05

  // 6) Token price for 20 % simple‐interest goal
  const priceFor20APY = yieldPoolUSD / (0.2 * totalStaked);      // ≃ $8.57

  const fmt = (n) => n.toLocaleString(undefined, { maximumFractionDigits: 2 });

  return (
    <div
      className="mx-auto p-6"
      style={{
        maxWidth: "1200px",
        fontFamily: "Chelsea Market",
        color: "black",
        boxSizing: "border-box",
      }}
    >
      <div className="yellow-box">
        <div className="heading-box">
          <h1>$PRIZE Staking Calculator</h1>
        </div>
        <div className="grid grid-cols-1 md:grid-cols-2 gap-8">

          {/* INPUT PANEL */}
          <div
            className="green-box"
            style={{ backgroundColor: "#FFF031", border: "none", boxShadow: "none" }}
          >
            {/* Total Staked */}
            <div style={{ display: "flex", flexDirection: "column", alignItems: "flex-start" }}>
              <label className="font-semibold text-blue-600 mb-1">
                Total $PRIZE Tokens Currently Staked
              </label>
              <div style={{ width: "100%", maxWidth: "500px" }}>
                <Input
                  type="text"
                  value={fmt(totalStaked)}
                  onChange={(e) => setTotalStaked(Number(e.target.value.replace(/,/g, "")))}
                  className="text-black"
                  style={{ fontSize: "1.3rem", padding: "10px", borderRadius: "8px", width: "100%" }}
                />
              </div>
              <p className="text-sm mt-2" style={{ fontSize: "0.9rem" }}>
                Enter the total number of $PRIZE tokens currently staked on the platform.
              </p>
            </div>
            <hr className="border-white" />

            {/* Your Stake */}
            <div style={{ display: "flex", flexDirection: "column", alignItems: "flex-start" }}>
              <label className="font-semibold text-blue-600 mb-1">
                Your $PRIZE Stake
              </label>
              <div style={{ width: "100%", maxWidth: "500px" }}>
                <Input
                  type="text"
                  value={fmt(userStake)}
                  onChange={(e) => setUserStake(Number(e.target.value.replace(/,/g, "")))}
                  className="text-black"
                  style={{ fontSize: "1.3rem", padding: "10px", borderRadius: "8px", width: "100%" }}
                />
              </div>
              <p className="text-sm mt-2" style={{ fontSize: "0.9rem" }}>
                Enter how many $PRIZE tokens you are considering to stake.
              </p>
            </div>
            <hr className="border-white" />

            {/* Token Price */}
            <div style={{ display: "flex", flexDirection: "column", alignItems: "flex-start" }}>
              <label className="font-semibold text-blue-600 mb-1">
                $PRIZE Token Price (USD)
              </label>
              <div style={{ width: "100%", maxWidth: "500px" }}>
                <Input
                  type="number"
                  value={tokenPrice}
                  onChange={(e) => setTokenPrice(parseFloat(e.target.value) || 0)}
                  className="text-black"
                  style={{ fontSize: "1.3rem", padding: "10px", borderRadius: "8px", width: "100%" }}
                />
              </div>
              <p className="text-sm mt-2" style={{ fontSize: "0.9rem" }}>
                Enter the current market price of one $PRIZE token in USD.
              </p>
            </div>
            <hr className="border-white" />

            {/* Industry Revenue Capture */}
            <div style={{ display: "flex", flexDirection: "column", alignItems: "flex-start" }}>
              <label className="font-semibold text-blue-600 mb-2">
                Industry Revenue Capture (% of $400 B)
              </label>
              <div style={{ width: "100%", maxWidth: "500px" }}>
                <Slider
                  min={2}
                  max={100}
                  step={1}
                  value={[industryPercent]}
                  onValueChange={(val) => setIndustryPercent(val[0])}
                  className="slider-field"
                />
              </div>
              <div className="mt-2">{industryPercent}%</div>
              <p className="text-sm mt-1" style={{ fontSize: "0.9rem" }}>
                Adjust to estimate what portion of the $400 B annual industry revenue the platform captures.
              </p>
            </div>
          </div>

          {/* RESULTS PANEL */}
          <div className="green-box" style={{ width: "100%" }}>
            <div
              className="text-2xl font-bold text-center text-white"
              style={{ fontSize: "2.5rem" }}
            >
              RESULTS
            </div>
            <hr className="border-white" />

            {/* Staking Revenue */}
            <div>
              <strong style={{ fontSize: "1.5rem" }}>
                Staking Revenue (USD, APY):
              </strong>
              <div
                style={{
                  color: "#1427B8",
                  fontSize: "1.5rem",
                  fontWeight: "bold",
                }}
              >
                ${fmt(apyRevenueUSD)}
              </div>
            </div>
            <hr className="border-white" />

            {/* APY % */}
            <div>
              <strong style={{ fontSize: "1.5rem" }}>APY (%):</strong>
              <div
                style={{
                  color: "#1427B8",
                  fontSize: "1.5rem",
                  fontWeight: "bold",
                }}
              >
                {fmt(apyPercent)}%
              </div>
              <p className="text-sm text-white" style={{ fontSize: "0.9rem" }}>
                Annual percentage yield, compounded daily.
              </p>
            </div>
            <hr className="border-white" />

            {/* Price for 20% */}
            <div>
              <strong style={{ fontSize: "1.5rem" }}>
                Token Price for 20% APY:
              </strong>
              <div
                style={{
                  color: "#1427B8",
                  fontSize: "1.5rem",
                  fontWeight: "bold",
                }}
              >
                ${fmt(priceFor20APY)}
              </div>
            </div>

            <button
              onClick={() => window.open("https://www.prizetech.xyz/buy-prize-ico", "_blank")}
              className="button"
              style={{
                padding: "12px 48px",
                width: "calc(28% * 3)",
                marginTop: "auto",
                marginBottom: "20px",
              }}
            >
              BUY $PRIZE
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}
