import React, { useState } from "react";
import { Slider } from "@/components/ui/slider";
import { Input } from "@/components/ui/input";

export default function PrizeStakingCalculator() {
  // State
  const [totalStaked, setTotalStaked]         = useState(700_000_000);
  const [userStake, setUserStake]             = useState(10000);
  const [industryPercent, setIndustryPercent] = useState(10);
  const [tokenPrice, setTokenPrice]           = useState(0.01);

  // Constants
  const INDUSTRY_TOTAL     = 400_000_000_000;             // $400B
  const BUSINESS_CUT       = 0.03;                        // 3¢ per $
  const MAX_REVENUE        = INDUSTRY_TOTAL * BUSINESS_CUT;// $12B
  const COMPOUNDS_PER_YEAR = 365;                         // daily

  // Annual simple‐interest revenue (flat APR)
  const annualRevenueUSD     = (industryPercent / 100) * MAX_REVENUE * (userStake / totalStaked);
  const initialInvestmentUSD = userStake * tokenPrice;

  // 1-day interest rate
  const dailyRate = annualRevenueUSD / initialInvestmentUSD / COMPOUNDS_PER_YEAR;

  // Daily-compounded APY
  const apyDecimal     = Math.pow(1 + dailyRate, COMPOUNDS_PER_YEAR) - 1;
  const apyPercent     = apyDecimal * 100;
  const apyRevenueUSD  = initialInvestmentUSD * apyDecimal;

  // Price for 20% simple APR (unchanged)
  const tokenPriceFor20APR = ((industryPercent / 100) * MAX_REVENUE / totalStaked) / 0.20;

  // Formatter
  const fmt = num => num.toLocaleString(undefined, { maximumFractionDigits: 2 });

  return (
    <div className="calculator-wrapper" style={{
      margin: '0 auto', padding: '1.5rem', maxWidth: '1200px',
      fontFamily: 'Chelsea Market', color: 'black', boxSizing: 'border-box'
    }}>
      <div className="yellow-box">
        <div className="heading-box"><h1>$PRIZE Staking Calculator</h1></div>
        <div className="grid grid-cols-1 md:grid-cols-2 gap-8">

          {/* Input Panel */}
          <div className="green-box" style={{ backgroundColor:'#FFF031', border:'none', boxShadow:'none' }}>
            {/* … your existing input fields (Total Staked, Your Stake, Token Price, Slider) unchanged … */}
          </div>

          {/* Results Panel */}
          <div className="green-box" style={{ width:'100%' }}>
            <div className="text-2xl font-bold text-center text-white" style={{ fontSize:'2.5rem' }}>
              RESULTS
            </div>
            <hr className="border-white" />

            {/* Flat APR Revenue */}
            <div>
              <strong style={{ fontSize:'1.5rem' }}>Staking Revenue (USD):</strong>
              <div style={{ color:'#1427B8', fontSize:'1.5rem', fontWeight:'bold' }}>
                ${fmt(annualRevenueUSD)}
              </div>
              <p className="text-sm text-white" style={{ fontSize:'0.9rem' }}>
                This is your annual revenue on a simple APR basis.
              </p>
            </div>
            <hr className="border-white" />

            {/* Daily-Compounded APY Revenue */}
            <div>
              <strong style={{ fontSize:'1.5rem' }}>Staking Revenue (USD, APY):</strong>
              <div style={{ color:'#1427B8', fontSize:'1.5rem', fontWeight:'bold' }}>
                ${fmt(apyRevenueUSD)}
              </div>
              <p className="text-sm text-white" style={{ fontSize:'0.9rem' }}>
                This is your annual revenue compounded daily (APY).
              </p>
            </div>
            <hr className="border-white" />

            {/* APY % */}
            <div>
              <strong style={{ fontSize:'1.5rem' }}>APY (%):</strong>
              <div style={{ color:'#1427B8', fontSize:'1.5rem', fontWeight:'bold' }}>
                {fmt(apyPercent)}%
              </div>
              <p className="text-sm text-white" style={{ fontSize:'0.9rem' }}>
                Your annual percentage yield, compounded daily.
              </p>
            </div>
            <hr className="border-white" />

            {/* Price for 20% Return */}
            <div>
              <strong style={{ fontSize:'1.5rem' }}>Price for 20% Return (USD):</strong>
              <div style={{ color:'#1427B8', fontSize:'1.5rem', fontWeight:'bold' }}>
                ${fmt(tokenPriceFor20APR)}
              </div>
              <p className="text-sm text-white" style={{ fontSize:'0.9rem' }}>
                Required token price for a 20% simple APR.
              </p>
            </div>

            <button
              onClick={() => window.open('https://www.prizetech.xyz/buy-prize-ico','_blank')}
              className="button"
              style={{
                padding:'12px 48px', width:'calc(28% * 3)',
                marginTop:'auto', marginBottom:'20px'
              }}
            >
              BUY $PRIZE
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}
