import React, { useState } from "react";
import { Slider } from "@/components/ui/slider";
import { Input } from "@/components/ui/input";

export default function PrizeStakingCalculator() {
  const [totalStaked, setTotalStaked]         = useState(700_000_000);
  const [userStake, setUserStake]             = useState(10000);
  const [industryPercent, setIndustryPercent] = useState(10);
  const [tokenPrice, setTokenPrice]           = useState(0.01);

  const INDUSTRY_TOTAL = 400_000_000_000;
  const BUSINESS_CUT   = 0.03;
  const MAX_REVENUE    = INDUSTRY_TOTAL * BUSINESS_CUT;

  const revenueCaptured      = (industryPercent / 100) * MAX_REVENUE;
  const userShare            = userStake / totalStaked;
  const userRevenue          = userShare * revenueCaptured;
  const initialInvestmentUSD = userStake * tokenPrice;
  const aprPercent           = initialInvestmentUSD > 0 ? (userRevenue / initialInvestmentUSD) * 100 : 0;
  const tokenPriceFor20APR   = (revenueCaptured / totalStaked) / 0.20;

  const formatNumber = (num, decimals = 2) => num.toLocaleString(undefined, { minimumFractionDigits: decimals, maximumFractionDigits: decimals });
  const formatInputValue = val => val.toLocaleString();
  const parseInputValue = str => Number(str.replace(/,/g, ""));

  return (
    <div className="calculator-wrapper" style={{ margin: '0 auto', padding: '1.5rem', maxWidth: '1200px', fontFamily: 'Chelsea Market', color: 'black', boxSizing: 'border-box' }}>
      {/* Yellow Input Panel */}
      <div className="green-box" style={{ backgroundColor: '#FFF031' }}>
        <div className="heading-box">
          <h1>$PRIZE Staking Calculator</h1>
        </div>

        {/* Total Staked Input */}
        <div style={{ marginBottom: '1rem' }}>
          <label className="font-semibold text-blue-600 mb-1">Total $PRIZE Tokens Currently Staked</label>
          <Input
            type="text"
            value={formatInputValue(totalStaked)}
            onChange={e => setTotalStaked(parseInputValue(e.target.value))}
            className="text-black"
            style={{ width: '100%', fontSize: '1.3rem', padding: '10px', borderRadius: '8px' }}
          />
          <p style={{ fontSize: '0.9rem', marginTop: '0.5rem' }}>
            Enter the total number of $PRIZE tokens currently staked on the platform.
          </p>
        </div>
        <hr className="border-white mb-4" />

        {/* User Stake Input */}
        <div style={{ marginBottom: '1rem' }}>
          <label className="font-semibold text-blue-600 mb-1">Your $PRIZE Stake</label>
          <Input
            type="text"
            value={formatInputValue(userStake)}
            onChange={e => setUserStake(parseInputValue(e.target.value))}
            className="text-black"
            style={{ width: '100%', fontSize: '1.3rem', padding: '10px', borderRadius: '8px' }}
          />
          <p style={{ fontSize: '0.9rem', marginTop: '0.5rem' }}>
            Enter how many $PRIZE tokens you are considering to stake.
          </p>
        </div>
        <hr className="border-white mb-4" />

        {/* Token Price Input */}
        <div style={{ marginBottom: '1rem' }}>
          <label className="font-semibold text-blue-600 mb-1">$PRIZE Token Price (USD)</label>
          <Input
            type="number"
            value={tokenPrice}
            onChange={e => setTokenPrice(parseFloat(e.target.value) || 0)}
            className="text-black"
            style={{ width: '100%', fontSize: '1.3rem', padding: '10px', borderRadius: '8px' }}
          />
          <p style={{ fontSize: '0.9rem', marginTop: '0.5rem' }}>
            Enter the current market price of one $PRIZE token in USD.
          </p>
        </div>
        <hr className="border-white mb-4" />

        {/* Industry Revenue Slider */}
        <div style={{ marginBottom: '1rem' }}>
          <label className="font-semibold text-blue-600 mb-1">Industry Revenue Capture (% of $400B)</label>
          <Slider
            min={2}
            max={100}
            step={1}
            value={[industryPercent]}
            onValueChange={val => setIndustryPercent(val[0])}
            className="slider-field"
          />
          <div style={{ marginTop: '0.5rem' }}>{industryPercent}%</div>
          <p style={{ fontSize: '0.9rem', marginTop: '0.5rem' }}>
            Adjust to estimate what portion of the $400B annual revenue the industry captures.
          </p>
        </div>
      </div>

      {/* Results Panel */}
      <div className="green-box" style={{ marginTop: '2rem', backgroundColor: '#34A853' }}>
        <div className="text-2xl font-bold text-center text-white mb-2">RESULTS</div>
        <hr className="border-white mb-4" />

        {/* Staking Revenue */}
        <div style={{ marginBottom: '1rem' }}>
          <strong>Staking Revenue (USD):</strong>
          <div style={{ fontSize: '1.5rem', fontWeight: 'bold', color: '#1427B8' }}>
            ${formatNumber(userRevenue, 2)}
          </div>
          <p style={{ fontSize: '0.9rem', marginTop: '0.5rem' }}>
            This is the estimated revenue in USD that your staked $PRIZE tokens would generate based on the captured market share.
          </p>
        </div>
        <hr className="border-white mb-4" />

        {/* APR */}
        <div style={{ marginBottom: '1rem' }}>
          <strong>APR (%):</strong>
          <div style={{ fontSize: '1.5rem', fontWeight: 'bold', color: '#1427B8' }}>
            {formatNumber(aprPercent, 2)}%
          </div>
          <p style={{ fontSize: '0.9rem', marginTop: '0.5rem' }}>
            This is your projected return on staked tokens as an annual percentage rate.
          </p>
        </div>
        <hr className="border-white mb-4" />

        {/* Price for 20% APR */}
        <div style={{ marginBottom: '1rem' }}>
          <strong>Price for 20% APR (USD):</strong>
          <div style={{ fontSize: '1.5rem', fontWeight: 'bold', color: '#1427B8' }}>
            ${formatNumber(tokenPriceFor20APR, 2)}
          </div>
          <p style={{ fontSize: '0.9rem', marginTop: '0.5rem' }}>
            This is the required token price to achieve a 20% APR across all stakers.
          </p>
        </div>

        <button
          onClick={() => window.open('https://www.prizetech.xyz/buy-prize-ico', '_blank')}
          className="button"
          style={{ display: 'block', margin: '2rem auto', padding: '12px 48px' }}
        >
          BUY $PRIZE
        </button>
      </div>
    </div>
  );
}
