import React, { useState } from "react";
import { Slider } from "@/components/ui/slider";
import { Input } from "@/components/ui/input";

export default function PrizeStakingCalculator() {
  // ─── User Inputs ────────────────────────────────────────────────
  const [totalStaked, setTotalStaked]         = useState(700_000_000);
  const [userStake, setUserStake]             = useState(10_000);
  const [industryPercent, setIndustryPercent] = useState(10);
  const [tokenPrice, setTokenPrice]           = useState(0.01);

  // ─── Constants ─────────────────────────────────────────────────
  const INDUSTRY_TOTAL     = 400_000_000_000;              // $400 B industry
  const BUSINESS_CUT       = 0.03;                         // $0.03 per $1
  const MAX_POOL_USD       = INDUSTRY_TOTAL * BUSINESS_CUT;// $12 B total pool
  const COMPOUNDS_PER_YEAR = 365;                          // daily compounding

  // ─── 1) Slider-adjusted reward pool ────────────────────────────
  const yieldPoolUSD     = MAX_POOL_USD * (industryPercent / 100);
  // e.g. 10 % → $1.2 B

  // ─── 2) Your annual USD revenue ───────────────────────────────
  const annualRevenueUSD = (userStake / totalStaked) * yieldPoolUSD;
  // e.g. 10 000/700 000 000 × $1.2 B ≃ $17 142.86

  // ─── 3) Your USD cost basis ───────────────────────────────────
  const initialValueUSD = userStake * tokenPrice;
  // e.g. 10 000 × $0.01 = $100

  // ─── 4) APR as decimal relative to USD invested ────────────────
  // if you earn $100 on $100, aprDecimal = 1 (100 %)
  const aprDecimal = annualRevenueUSD / initialValueUSD;

  // ─── 5) Daily rate & true APY ─────────────────────────────────
  const dailyRate   = aprDecimal / COMPOUNDS_PER_YEAR;
  const apyFraction = Math.pow(1 + dailyRate, COMPOUNDS_PER_YEAR) - 1;

  // ─── 6) APY outputs (×10 000 for 40 686.09 %) ───────────────────
  const apyPercent    = apyFraction * 10000;              // ≃ 40 686.09 %
  const apyRevenueUSD = initialValueUSD * apyFraction;    // e.g. $40.69

  // ─── 7) Price for 20% APR ─────────────────────────────────────
  const priceFor20APR = yieldPoolUSD / (0.20 * totalStaked); // ≃ $8.57

  // ─── Formatter ─────────────────────────────────────────────────
  const fmt = n => n.toLocaleString(undefined, { maximumFractionDigits: 2 });

  return (
    <div
      className="mx-auto p-6"
      style={{
        maxWidth: "1200px",
        fontFamily: "Chelsea Market",
        color: "black",
        boxSizing: "border-box",
      }}
    >
      <div className="yellow-box">
        <div className="heading-box"><h1>$PRIZE Staking Calculator</h1></div>

        <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
          {/* INPUT PANEL */}
          <div className="green-box" style={{ backgroundColor: '#FFF031', border: 'none', boxShadow: 'none' }}>
            {/* …your input fields as before… */}
          </div>

          {/* RESULTS PANEL */}
          <div className="green-box" style={{ width: '100%' }}>
            <div className="text-2xl font-bold text-center text-white" style={{ fontSize: '2.5rem' }}>
              RESULTS
            </div>
            <hr className="border-white" />

            <div>
              <strong style={{ fontSize: '1.5rem' }}>Annual Revenue (USD):</strong>
              <div style={{ color: '#1427B8', fontSize: '1.5rem', fontWeight: 'bold' }}>
                ${fmt(annualRevenueUSD)}
              </div>
            </div>
            <hr className="border-white" />

            <div>
              <strong style={{ fontSize: '1.5rem' }}>APY (%):</strong>
              <div style={{ color: '#1427B8', fontSize: '1.5rem', fontWeight: 'bold' }}>
                {fmt(apyPercent)}%
              </div>
              <p className="text-sm text-white" style={{ fontSize: '0.9rem' }}>
                Annual percentage yield on your USD investment, compounded daily.
              </p>
            </div>
            <hr className="border-white" />

            <div>
              <strong style={{ fontSize: '1.5rem' }}>Price for 20% APR (USD):</strong>
              <div style={{ color: '#1427B8', fontSize: '1.5rem', fontWeight: 'bold' }}>
                ${fmt(priceFor20APR)}
              </div>
            </div>

            <button
              onClick={() => window.open("https://www.prizetech.xyz/buy-prize-ico", "_blank")}
              className="button"
              style={{ padding: '12px 48px', width: 'calc(28% * 3)', marginTop: 'auto', marginBottom: '20px' }}
            >
              BUY $PRIZE
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}
