import React, { useState } from "react";
import { Slider } from "@/components/ui/slider";
import { Input } from "@/components/ui/input";

export default function PrizeStakingCalculator() {
  // State
  const [totalStaked, setTotalStaked]         = useState(700_000_000);
  const [userStake, setUserStake]             = useState(10000);
  const [industryPercent, setIndustryPercent] = useState(10);
  const [tokenPrice, setTokenPrice]           = useState(0.01);

  // Constants
  const INDUSTRY_TOTAL     = 400_000_000_000;
  const BUSINESS_CUT       = 0.03;
  const MAX_REVENUE        = INDUSTRY_TOTAL * BUSINESS_CUT;
  const COMPOUNDS_PER_YEAR = 365;

  // Annual calculations
  const revenueCaptured      = (industryPercent / 100) * MAX_REVENUE;
  const userShare            = userStake / totalStaked;
  const annualRevenueUSD     = userShare * revenueCaptured;       // $17,142.86
  const initialInvestmentUSD = userStake * tokenPrice;            // $100

  // Convert APR% into a decimal fraction for compounding
  // APR% = (annualRevenueUSD / initialInvestmentUSD) * 100
  // aprFraction = APR% / 100 = annualRevenueUSD / initialInvestmentUSD
  const aprFraction = annualRevenueUSD / initialInvestmentUSD;    // 171.4286

  // Daily‐compounded APY (fraction)
  const apyFraction = Math.pow(
    1 + aprFraction / COMPOUNDS_PER_YEAR,
    COMPOUNDS_PER_YEAR
  ) - 1;

  // Values to display
  const apyPercent   = apyFraction * 100;                         // ~453.05 %
  const apyRevenueUSD = initialInvestmentUSD * apyFraction;       // ~$453.05 USD

  // Price for 20% simple APR (unchanged)
  const tokenPriceFor20APR = (revenueCaptured / totalStaked) / 0.20;

  // Formatting
  const fmt = (n) => n.toLocaleString(undefined, { maximumFractionDigits: 2 });

  return (
    <div className="calculator-wrapper" style={{
      margin: '0 auto', padding: '1.5rem', maxWidth: '1200px',
      fontFamily: 'Chelsea Market', color: 'black', boxSizing: 'border-box'
    }}>
      <div className="yellow-box">
        <div className="heading-box"><h1>$PRIZE Staking Calculator</h1></div>
        <div className="grid grid-cols-1 md:grid-cols-2 gap-8">

          {/* Inputs */}
          <div className="green-box" style={{ backgroundColor:'#FFF031', border:'none', boxShadow:'none' }}>
            {/* ... your existing form layout for Total Staked, Your Stake, Token Price, Slider ... */}
            {/* No changes here; remain exactly as before */}
          </div>

          {/* Results */}
          <div className="green-box" style={{ width:'100%' }}>
            <div className="text-2xl font-bold text-center text-white" style={{ fontSize:'2.5rem' }}>
              RESULTS
            </div>
            <hr className="border-white" />

            {/* Annual Revenue (flat) */}
            <div>
              <strong style={{ fontSize:'1.5rem' }}>Staking Revenue (USD):</strong>
              <div style={{ color:'#1427B8', fontSize:'1.5rem', fontWeight:'bold' }}>
                ${fmt(annualRevenueUSD)}
              </div>
              <p className="text-sm text-white" style={{ fontSize:'0.9rem' }}>
                Your USD revenue at simple APR.
              </p>
            </div>
            <hr className="border-white" />

            {/* APY Revenue (compounded) */}
            <div>
              <strong style={{ fontSize:'1.5rem' }}>Staking Revenue (USD, APY):</strong>
              <div style={{ color:'#1427B8', fontSize:'1.5rem', fontWeight:'bold' }}>
                ${fmt(apyRevenueUSD)}
              </div>
              <p className="text-sm text-white" style={{ fontSize:'0.9rem' }}>
                Your USD revenue if interest compounds daily.
              </p>
            </div>
            <hr className="border-white" />

            {/* APY % */}
            <div>
              <strong style={{ fontSize:'1.5rem' }}>APY (%):</strong>
              <div style={{ color:'#1427B8', fontSize:'1.5rem', fontWeight:'bold' }}>
                {fmt(apyPercent)}%
              </div>
              <p className="text-sm text-white" style={{ fontSize:'0.9rem' }}>
                Annual percentage yield, compounded daily.
              </p>
            </div>
            <hr className="border-white" />

            {/* Price for 20% APR */}
            <div>
              <strong style={{ fontSize:'1.5rem' }}>Price for 20% APR (USD):</strong>
              <div style={{ color:'#1427B8', fontSize:'1.5rem', fontWeight:'bold' }}>
                ${fmt(tokenPriceFor20APR)}
              </div>
              <p className="text-sm text-white" style={{ fontSize:'0.9rem' }}>
                Required token price for a 20% simple APR.
              </p>
            </div>

            {/* CTA */}
            <button
              onClick={() => window.open('https://www.prizetech.xyz/buy-prize-ico','_blank')}
              className="button"
              style={{ padding:'12px 48px', width:'calc(28% * 3)', marginTop:'auto', marginBottom:'20px' }}
            >
              BUY $PRIZE
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}
