import React, { useState } from "react";
import { Slider } from "@/components/ui/slider";
import { Input } from "@/components/ui/input";

export default function PrizeStakingCalculator() {
  // ─── User Inputs ────────────────────────────────────────────────
  const [totalStaked, setTotalStaked]         = useState(700_000_000);
  const [userStake, setUserStake]             = useState(10_000);
  const [industryPercent, setIndustryPercent] = useState(10);
  const [tokenPrice, setTokenPrice]           = useState(0.01);

  // ─── Constants ─────────────────────────────────────────────────
  const INDUSTRY_TOTAL     = 400_000_000_000;              // $400 B
  const BUSINESS_CUT       = 0.03;                         // 3¢ per $1
  const MAX_POOL_USD       = INDUSTRY_TOTAL * BUSINESS_CUT;// $12 B total pool
  const COMPOUNDS_PER_YEAR = 365;                          // daily compounding

  // ─── 1) Slider-adjusted pool ──────────────────────────────────
  const yieldPoolUSD     = MAX_POOL_USD * (industryPercent / 100);
  // e.g. 10% ⇒ $1.2 B

  // ─── 2) Your annual USD revenue ───────────────────────────────
  const annualRevenueUSD = (userStake / totalStaked) * yieldPoolUSD;
  // e.g. 10 000/700 000 000 × $1.2 B ≃ $17 142.86

  // ─── 3) Your USD investment ───────────────────────────────────
  const initialValueUSD = userStake * tokenPrice;
  // e.g. 10 000 × $0.01 = $100

  // ─── 4) APR decimal (relative to USD invested) ────────────────
  // if annualRevenueUSD = initialValueUSD, APR = 1 (100%)
  const aprDecimal = annualRevenueUSD / initialValueUSD;

  // ─── 5) Daily rate & APY fraction ─────────────────────────────
  const dailyRate   = aprDecimal / COMPOUNDS_PER_YEAR;
  const apyFraction = Math.pow(1 + dailyRate, COMPOUNDS_PER_YEAR) - 1;

  // ─── 6) APY outputs ─────────────────────────────────────────────
  const apyPercent    = apyFraction * 100;               // percent of USD value
  const apyRevenueUSD = initialValueUSD * apyFraction;   // USD earnings with compounding

  // ─── 7) Price for 20% APR (unchanged) ─────────────────────────
  const priceFor20APR = yieldPoolUSD / (0.20 * totalStaked);

  // ─── Formatter ─────────────────────────────────────────────────
  const fmt = n => n.toLocaleString(undefined, { maximumFractionDigits: 2 });

  return (
    <div
      className="mx-auto p-6"
      style={{
        maxWidth: "1200px",
        fontFamily: "Chelsea Market",
        color: "black",
        boxSizing: "border-box"
      }}
    >
      <div className="yellow-box">
        <div className="heading-box"><h1>$PRIZE Staking Calculator</h1></div>

        <div className="grid grid-cols-1 md:grid-cols-2 gap-8">

          {/* INPUTS */}
          <div className="green-box" style={{ backgroundColor: "#FFF031", border: "none", boxShadow: "none" }}>
            <div style={{ color: "#1427B8", fontSize: "1.5rem" }} className="space-y-8 pt-2">
              {/* Total Staked */}
              <div style={{ display: "flex", flexDirection: "column", alignItems: "flex-start" }}>
                <label className="font-semibold text-blue-600 mb-1">
                  Total $PRIZE Tokens Currently Staked
                </label>
                <div style={{ width: "100%", maxWidth: "500px" }}>
                  <Input
                    type="text"
                    value={fmt(totalStaked)}
                    onChange={e => setTotalStaked(Number(e.target.value.replace(/,/g, "")))}
                    className="text-black"
                    style={{ fontSize: "1.3rem", padding: "10px", borderRadius: "8px", width: "100%" }}
                  />
                </div>
                <p className="text-sm mt-2" style={{ fontSize: "0.9rem" }}>
                  Enter total $PRIZE tokens staked.
                </p>
              </div>
              <hr className="border-white"/>

              {/* Your Stake */}
              <div style={{ display: "flex", flexDirection: "column", alignItems: "flex-start" }}>
                <label className="font-semibold text-blue-600 mb-1">
                  Your $PRIZE Stake
                </label>
                <div style={{ width: "100%", maxWidth: "500px" }}>
                  <Input
                    type="text"
                    value={fmt(userStake)}
                    onChange={e => setUserStake(Number(e.target.value.replace(/,/g, "")))}
                    className="text-black"
                    style={{ fontSize: "1.3rem", padding: "10px", borderRadius: "8px", width: "100%" }}
                  />
                </div>
                <p className="text-sm mt-2" style={{ fontSize: "0.9rem" }}>
                  Enter how many $PRIZE tokens to stake.
                </p>
              </div>
              <hr className="border-white"/>

              {/* Token Price */}
              <div style={{ display: "flex", flexDirection: "column", alignItems: "flex-start" }}>
                <label className="font-semibold text-blue-600 mb-1">
                  $PRIZE Token Price (USD)
                </label>
                <div style={{ width: "100%", maxWidth: "500px" }}>
                  <Input
                    type="number"
                    value={tokenPrice}
                    onChange={e => setTokenPrice(parseFloat(e.target.value) || 0)}
                    className="text-black"
                    style={{ fontSize: "1.3rem", padding: "10px", borderRadius: "8px", width: "100%" }}
                  />
                </div>
                <p className="text-sm mt-2" style={{ fontSize: "0.9rem" }}>
                  Only used for “Price for 20% APR” below.
                </p>
              </div>
              <hr className="border-white"/>

              {/* Revenue Slider */}
              <div style={{ display: "flex", flexDirection: "column", alignItems: "flex-start" }}>
                <label className="font-semibold text-blue-600 mb-2">
                  Industry Revenue Capture (% of $400 B)
                </label>
                <div style={{ width: "100%", maxWidth: "500px" }}>
                  <Slider
                    min={2} max={100} step={1}
                    value={[industryPercent]}
                    onValueChange={val => setIndustryPercent(val[0])}
                    className="slider-field"
                  />
                </div>
                <div className="mt-2">{industryPercent}%</div>
                <p className="text-sm mt-1" style={{ fontSize: "0.9rem" }}>
                  Portion of the $400 B industry revenue captured.
                </p>
              </div>
            </div>
          </div>

          {/* RESULTS */}
          <div className="green-box" style={{ width: "100%" }}>
            <div className="text-2xl font-bold text-center text-white" style={{ fontSize: "2.5rem" }}>
              RESULTS
            </div>
            <hr className="border-white"/>

            {/* Annual Revenue */}
            <div>
              <strong style={{ fontSize: "1.5rem" }}>Annual Revenue (USD):</strong>
              <div style={{ color: "#1427B8", fontSize: "1.5rem", fontWeight: "bold" }}>
                ${fmt(annualRevenueUSD)}
              </div>
              <p className="text-sm text-white" style={{ fontSize: "0.9rem" }}>
                Your yearly USD revenue from your share of the pool.
              </p>
            </div>
            <hr className="border-white"/>

            {/* APY */}
            <div>
              <strong style={{ fontSize: "1.5rem" }}>APY (%):</strong>
              <div style={{ color: "#1427B8", fontSize: "1.5rem", fontWeight: "bold" }}>
                {fmt(apyPercent)}%
              </div>
              <p className="text-sm text-white" style={{ fontSize: "0.9rem" }}>
                Annual percentage yield on your USD investment, compounded daily.
              </p>
            </div>
            <hr className="border-white"/>

            {/* Price for 20% APR */}
            <div>
              <strong style={{ fontSize: "1.5rem" }}>Price for 20% APR (USD):</strong>
              <div style={{ color: "#1427B8", fontSize: "1.5rem", fontWeight: "bold" }}>
                ${fmt(priceFor20APR)}
              </div>
              <p className="text-sm text-white" style={{ fontSize: "0.9rem" }}>
                Required token price to achieve a 20% APR across all stakers.
              </p>
            </div>

            <button
              onClick={() => window.open("https://www.prizetech.xyz/buy-prize-ico", "_blank")}
              className="button"
              style={{ padding: "12px 48px", width: "calc(28% * 3)", marginTop: "auto", marginBottom: "20px" }}
            >
              BUY $PRIZE
            </button>
          </div>

        </div>
      </div>
    </div>
  );
